sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	//constants
	var constants = {
		WHAT_NEEDED: {value:'u_what_needed',writable:false}, 
		RETURN_NAME: {value:'whatNeededValues',writable:false},
		SCRIPT_INCLUDE_NAME: {value:'GetWhatNeededValues',writable:false},
		METHOD_NAME: {value:'getWhatNeededValues',writable:false},
		LABEL: {value:'label',writable:false},
		ID: {value:'id',writable:false}
	};

	//add options to formElem based on the the property of this object that matches the selected newValue
	Array.prototype.addOptionsToDropdown = function(formElem,label,id){
		g_form.clearOptions(formElem);
		if (this !== null && this !== '' && this !== undefined) {
			for (var iter = 0; iter < this.length; iter++){
				g_form.addOption(formElem,this[iter][id],this[iter][label]);
			}
		}
	};

	var parseResponse = function(response){
		//returns the property (which is an array) named constants.RETURN_NAME.value of the JSON object 
		var requestsTypes = JSON.parse(
			response.responseXML.documentElement.getAttribute("answer"))[constants.RETURN_NAME.value];
		if (Array.isArray(requestsTypes)) {
			requestsTypes.addOptionsToDropdown(constants.WHAT_NEEDED.value,
											   constants.LABEL.value,constants.ID.value);
		}
		Array.prototype.addOptionsToDropdown = null;
	};
	var getWhatNeededValues = new GlideAjax(constants.SCRIPT_INCLUDE_NAME.value);
	getWhatNeededValues.addParam('sysparm_name',constants.METHOD_NAME.value);
	getWhatNeededValues.addParam('sysparm_returnName',constants.RETURN_NAME.value);
	getWhatNeededValues.addParam('sysparm_requestType', newValue);
	getWhatNeededValues.getXML(parseResponse);

}
