sys_script_include.script:
var GetWhatNeededValues = Class.create();
GetWhatNeededValues.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	//constants
	WHAT_NEEDED_TABLE: {value: 'x_278456_needit_what_needed_values',writable: false},
	REQUEST_TYPE_ID_FIELD: {value: 'u_request_type_id',writable: false},
	LABEL_FIELD: {value: 'label',writable: false},
	ID_FIELD: {value: 'id',writable: false},

	//return the appropriate What needed values for the Request type
	getWhatNeededValues: function() {

		var parameters = {
			//@String - the request type id in the What needed table
			REQUEST_TYPE: {value: this.getParameter('sysparm_requestType'), writable:false},
			//@String - the name of the JSON object to pass back to the client script
		    RETURN_NAME: {value: this.getParameter('sysparm_returnName'), writable:false}
		};		
		var values = [];
		var reqTypeId = new GetRequestType().getRequestTypeSysId(parameters.REQUEST_TYPE.value);
		var simpleGlideRecordQuery = new x_278456_queryutil.SimpleGlideRecordQuery();
		var gr = simpleGlideRecordQuery.getSimpleGlideRecordQuery(
					this.WHAT_NEEDED_TABLE.value,
					[{
						'field' : this.REQUEST_TYPE_ID_FIELD.value,
						'value' : reqTypeId
					}],
					this.LABEL_FIELD.value);

		//add the label and id for each What needed value that matches this Request type
		while(gr.next()){
			//pass it as an array of stringified json objects
			values.push(JSON.stringify({
				'label': gr.getValue(this.LABEL_FIELD.value),
				'id'   : gr.getValue(this.ID_FIELD.value)
			}));
		}
		//pass back a string representation of a json object containing the array
		return '{"'+parameters.RETURN_NAME.value+'":['+values.toString()+']}';
	}	
});
