sys_script_include.script:
var GetRequestType = Class.create();
GetRequestType.prototype = {

	initialize: function(){

	},

	//constants
	REQUEST_TYPES_TABLE: {value: 'x_278456_needit_request_types',writable: false},
	REQUEST_TYPE_ID_FIELD: {value: 'u_request_type_id',writable: false},

    //get the guid for the Request type from the Request type table
	//@String reqType - Request type id
	getRequestTypeSysId: function(reqType){
		var gr = new GlideRecord(this.REQUEST_TYPES_TABLE.value);
		gr.get(this.REQUEST_TYPE_ID_FIELD.value,reqType);
		return gr.sys_id;
	},
    type: 'GetRequestType'
};
